sys_atf_step_config.description_generator:
function generateDescription() {
    // the global variable 'step' represents the current glide record
    var description = "An request will be generated for item "+step.inputs.u_case_number.catalog_item.name+" for case number "+step.inputs.u_case_number.number;
	// your code here
	return description;
}
 generateDescription();

sys_atf_step_config.step_execution_generator:
(function executeStep(inputs, outputs, stepResult, timeout) {

    var tCase = inputs.u_case_number;
    try {
        var req = new x_172493_jit_atf.JITAtfUtils().createItem(tCase);
		if(req.toString() != "Error in Execution of Request"){
        var cRequest = new GlideRecord('sc_request');
			cRequest.get(req);
			//outputs.table = 'sc_request';
            outputs.u_request = cRequest.getUniqueValue();
			var ritm = new GlideRecord('sc_req_item');
			ritm.addQuery('request',cRequest.getUniqueValue());
			ritm.query();
			if(ritm.next()){
				outputs.u_ritm = ritm.getUniqueValue();
			}
        stepResult.setOutputMessage('Success!');
        stepResult.setSuccess();
        return;
		}
		else{
		stepResult.setOutputMessage('Error in Execution of Request');
        stepResult.setFailed();
        return;	
		}
    } catch (e) {
        stepResult.setOutputMessage('Failure!');
        stepResult.setFailed();
        return;
    }
}(inputs, outputs, stepResult, timeout));
