sys_atf_step_config.description_generator:
function generateDescription() {
    // the global variable 'step' represents the current glide record
    var description = "The approval Records for the task will be "+step.inputs.u_approval_action.getDisplayValue();
	// your code here
	return description;
}
 generateDescription();

sys_atf_step_config.step_execution_generator:
(function executeStep(inputs, outputs, stepResult, timeout) {

    var taskApp = inputs.u_bulk_approval_task;
	var appAction = inputs.u_approval_action;
    try {
        if (taskApp != '') {
            var app = new GlideRecord('sysapproval_approver');
            app.addQuery('document_id', taskApp);
            app.addQuery('state', 'requested');
            app.query();
            while (app.next()) {
                app.state = appAction;
                app.update();
            }
            stepResult.setOutputMessage('Success!');
            stepResult.setSuccess();
            return;
        }
    } catch (e) {
        stepResult.setOutputMessage('Failure!');
        stepResult.setFailed();
        return;
    }

}(inputs, outputs, stepResult, timeout));
