sys_atf_step_config.description_generator:
function generateDescription() {
    // the global variable 'step' represents the current glide record
    var description = "The approval Record for the task will be "+step.inputs.u_approval_action.getDisplayValue();
	// your code here
	return description;
}
 generateDescription();

sys_atf_step_config.step_execution_generator:
(function executeStep(inputs, outputs, stepResult, timeout) {
    var testApp = inputs.u_task.toString();
    var appAction = inputs.u_approval_action;
    try {
        var appSc = new x_172493_jit_atf.JITAtfUtils().singleServerApproval(testApp, appAction);
        if (appSc == true) {
            stepResult.setOutputMessage('Success!' + appAction);
            stepResult.setSuccess();
            return;
        } else {
            stepResult.setOutputMessage('Failure! No Approval Record found');
            stepResult.setFailed();
            return;
        }
    } catch (e) {
        stepResult.setOutputMessage('Failure! exception caught.');
        stepResult.setFailed();
        return;

    }

}(inputs, outputs, stepResult, timeout));
