sys_atf_step_config.description_generator:
function generateDescription() {
    // the global variable 'step' represents the current glide record
	var caseNumber = step.inputs.u_case.number;
	var caseTable = step.inputs.u_case.table;

    var description = "A record in table "+caseTable+" will be generated for Test case number "+caseNumber;
	// your code here
	return description;
}
 generateDescription();

sys_atf_step_config.step_execution_generator:
(function executeStep(inputs, outputs, stepResult, timeout) {

    var aCase = inputs.u_case;
    try {
        var caseRec = new x_172493_jit_atf.JITAtfUtils().createTableRec(aCase);
        var arr = caseRec.split('^');
        if (arr[0].toString().length == 32) {
            outputs.u_table = arr[1];
            outputs.u_first_record = arr[0];
            stepResult.setOutputMessage('Success!');
            stepResult.setSuccess();
            return;
        } else {
            stepResult.setOutputMessage('Failure!  ' + caseRec);
            stepResult.setFailed();
            return;
        }
    } catch (e) {
        stepResult.setOutputMessage('Failure!  ' + e);
        stepResult.setFailed();
        return;
    }
}(inputs, outputs, stepResult, timeout));
