sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    if (newValue == '') {
        g_form.setMandatory('catalog_item', false);
        g_form.setValue('catalog_item', '');
        g_form.setDisplay('catalog_item', false);
        g_form.setValue('field_values', '');
        g_form.setDisplay('field_values', false);
        g_form.setValue('requested_for', '');
        g_form.setDisplay('requested_for', false);
        g_form.setMandatory('table', false);
        g_form.setValue('table', '');
        g_form.setDisplay('table', false);
        g_form.setValue('variable_json', '');
        g_form.setDisplay('variable_json', false);
    } else if (newValue == 'service_catalog') {
        g_form.setDisplay('catalog_item', true);
        g_form.setMandatory('catalog_item', true);
        g_form.setValue('field_values', '');
        g_form.setDisplay('field_values', false);
        g_form.setDisplay('requested_for', true);
        g_form.setMandatory('table', false);
        g_form.setValue('table', '');
        g_form.setDisplay('table', false);
        g_form.setDisplay('variable_json', true);
    } else if (newValue == 'table') {

        g_form.setMandatory('catalog_item', false);
        g_form.setValue('catalog_item', '');
        g_form.setDisplay('catalog_item', false);
        g_form.setDisplay('field_values', true);
        g_form.setValue('requested_for', '');
        g_form.setDisplay('requested_for', false);
        g_form.setDisplay('table', true);
        g_form.setMandatory('table', true);
        g_form.setValue('variable_json', '');
        g_form.setDisplay('variable_json', false);
    }

}
